mapper.insert:
 insert into
road_damage_table
 values (#{damage_code},(select worker_code from worker where name = #{woker_code_name}),#{regular_code},#{road_name},#{check_date}
 ,#{begin_end},#{length},#{width},#{damage_type},#{d_lenth},#{d_width},
 #{d_height},#{d_position_description},#{d_areas},#{remark},#{manage_unite},#{make_date});

mapper.select:
select * from road_damage_table where road_name=#{road_name}
and  check_date>=#{history}
and  check_date<=#{today} ;
