mapper.update:
update
road_surface_table
set worker_code=(select worker_code from worker where name = #{worker_code_name}),road_name=#{road_name},
check_date=#{check_date},IRI=#{IRI},remark=#{remark}
where surface_code=#{surface_code};

mapper.select:
select * from road_surface_table where road_name=#{road_name}
and  check_date>=#{history}
and  check_date<=#{today} ;
